  ],
})
export class ExtraModule {}

import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import {MyCounterComponent} from "./my-counter-comp/my-counter-comp.component";
import {BrowserModule} from "@angular/platform-browser";
import {counterReducer} from "./counter.reducer";
import {StoreModule} from "@ngrx/store";

@NgModule({
  declarations: [MyCounterComponent],
  exports: [MyCounterComponent],
  imports: [
    CommonModule,
    BrowserModule,
    StoreModule.forRoot({ count: counterReducer })
  ],
  providers: []
})
export class MyCounterModule {}

import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import {BlogComponent} from "./blog/blog.component";
import {BlogDetailComponent} from "./blog/blog-detail/blog-detail.component";
import {AboutComponent} from "./about/about.component";
import {RelayOnComponent} from "./about/About-Components/relay-on/relay-on.component";
import {TopContentComponent} from "./about/About-Components/top-content/top-content.component";
import {FullComponent} from "./layout/full/full.component";
import {BannerComponent} from "./shared/banner/banner.component";
import {BannerNavigationComponent} from "./shared/banner-navigation/banner-navigation.component";
import {FooterComponent} from "./shared/footer/footer.component";
import {RouterLinkActive, RouterOutlet} from "@angular/router";
import {PagesRoutingModule} from "./pages-routing.module";
import {BannerContentComponent} from "./shared/banner-content/banner-content.component";

@NgModule({
  imports: [CommonModule, RouterLinkActive, RouterOutlet],
  exports: [
    FullComponent,
    FooterComponent,
    BannerComponent,
    PagesRoutingModule
  ],
  declarations: [
    BlogComponent,
    AboutComponent,
    BlogDetailComponent,
    RelayOnComponent,
    TopContentComponent,
    FullComponent,
    BannerComponent,
    BannerContentComponent,
    BannerNavigationComponent,
    FooterComponent,
  ]
})
export class PagesModule {}

import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';

import { BlogComponent } from './blog/blog.component';
import { AboutComponent } from './about/about.component';
import { BlogDetailComponent } from './blog/blog-detail/blog-detail.component';


import { FullComponent } from './layout/full/full.component';


const routes: Routes = [
  {
    path: "",
    component: FullComponent,
    children: [
      { path: "", component: BlogComponent },
      { path: "blogDetail/:id", component: BlogDetailComponent },
      { path: "about", component: AboutComponent },
    ]
  }
];

@NgModule({
  imports: [RouterModule.forChild(routes)],
  exports: [RouterModule]
})
export class PagesRoutingModule { }

import { NgModule } from '@angular/core';
import { CommonModule } from '@angular/common';
import { TechnologiesRoutingModule } from './technologies-routing.module';
import { CategoryDetailsComponent } from './technologies/category-details/category-details.component';
import {MatCardModule} from "@angular/material/card";
import {PagesModule} from "@lda/pages";
import {MatTableModule} from "@angular/material/table";
import {MatPaginatorModule} from "@angular/material/paginator";
import {MatFormFieldModule} from "@angular/material/form-field";
import {MatIconModule} from "@angular/material/icon";
import {FormsModule} from "@angular/forms";
import {MatInputModule} from "@angular/material/input";
import {MatButtonModule} from "@angular/material/button";
import {ButtonModule} from "primeng/button";
import {ListComponent} from "./technologies/list/list.component";
import {TechnologiesComponent} from "./technologies/technologies.component";

@NgModule({
  imports: [
    CommonModule,
    TechnologiesRoutingModule,
    MatCardModule,
    MatTableModule,
    MatPaginatorModule,
    MatFormFieldModule,
    MatIconModule,
    FormsModule,
    MatInputModule,
    MatButtonModule,
    ButtonModule,
    PagesModule
  ],
  declarations: [
    TechnologiesComponent,
    ListComponent,
    CategoryDetailsComponent,
  ],
  exports: [],
})
export class TechnologiesModule {}

import { NgModule } from '@angular/core';
import { Routes, RouterModule } from '@angular/router';
import {ListComponent} from "./technologies/list/list.component";
import {TechnologiesComponent} from "./technologies/technologies.component";

const routes: Routes = [
  { path: "", redirectTo: "tech", pathMatch: "full" },
  { path:"tech", component:TechnologiesComponent, pathMatch: "full" },
  { path:"tech/list", component:ListComponent, pathMatch: "full" },
  { path:"tech/details",
    children: [
      { path: "", loadChildren: () => "import('../../../pages/src/lib/pages.module').then(m => m.PagesModule)" }
    ]}
];

@NgModule({
  imports: [RouterModule.forChild(routes)],
  exports: [RouterModule]
})
export class TechnologiesRoutingModule { }

